    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        double suhu = Double.parseDouble(txtInput.getText());
        double hasil = 0;

        if (!radioCelciusToFahrenheit.isSelected()) {
            if (radioFahrenheitToCelcius.isSelected()) {
                hasil = (suhu - 32) * 5/9; // Fahrenheit ke Celcius
            }
        } else {
            hasil = (suhu * 9/5) + 32; // Celcius ke Fahrenheit
        } 
// Tambahkan logika untuk skala lain
lblHasil.setText(String.format("Hasil: %.2f", hasil));        // TODO add your handling code here:
    }//GEN-LAST:event_jButton1ActionPerformed

